export default {
  name: 'Echarts',
  data: function data() {
    return {
      seriesdata: this.stopCArrayData
    };
  },
  watch: {
    seriesdata: function seriesdata(newVal) {
      this.seriesdata = newVal; //对父组件传过来的值进行监听，如果改变也对子组件内部的值进行改变

      this.myEchartsInit();
    }
  },
  methods: {
    myEchartsInit: function myEchartsInit() {
      // 2.基于准备好的dom，初始化echarts实例
      var myChart = this.$echarts.init(document.getElementById('main')); //3.配置数据

      var showLable = true;
      var option = {
        title: {
          text: 'stopC 迭代过程'
        },
        tooltip: {},
        legend: {
          data: ['stopC']
        },
        xAxis: {
          type: 'category',
          name: '迭代轮数',
          data: [1, 2, 3]
        },
        yAxis: {
          type: 'value',
          name: '迭代损失'
        },
        //Y轴
        series: [{
          name: 'stopC',
          type: 'bar',
          label: {
            normal: {
              show: showLable,
              position: 'inside'
            }
          },
          data: this.seriesdata
        }],
        dataZoom: [{
          type: "slider",
          show: true,
          xAxisIndex: [0],
          start: 0,
          end: 100,
          textStyle: {
            color: "#ccd7d7"
          }
        }]
      }; // 4.传入数据

      myChart.setOption(option);
    }
  },
  mounted: function mounted() {
    this.myEchartsInit();
  }
};
