export default {
  name: 'Algorithm',
  data: function data() {
    return {
      method: "",
      parallelism: "",
      jobname: "",
      stopCArray: [],
      stopCIndex: [],
      auc: "",
      time: ""
    };
  },
  methods: {
    runMethodBtn: function runMethodBtn() {
      var _this = this;

      this.axios({
        method: 'get',
        url: 'http://192.168.2.47:8888/run/?method=' + this.method + '&parallelism=' + this.parallelism + "&jobname=" + this.jobname
      }).then(function (res) {
        _this.stopCArray = res.data.stopC;
        _this.auc = res.data.auc;
        _this.time = res.data.time;

        for (var i = 1; i <= _this.stopCArray.length; i++) {
          _this.stopCIndex[i - 1] = i;
        }

        _this.myEchartsInit();
      });
    },
    myEchartsInit: function myEchartsInit() {
      // 2.基于准备好的dom，初始化echarts实例
      var myChart = this.$echarts.init(document.getElementById('main')); //3.配置数据

      var showLable = true;
      var option = {
        title: {
          text: 'stopC 迭代过程'
        },
        tooltip: {},
        legend: {
          data: ['stopC']
        },
        xAxis: {
          type: 'category',
          name: '迭代轮数',
          data: this.stopCIndex
        },
        yAxis: {
          type: 'value',
          name: '迭代损失'
        },
        //Y轴
        series: [{
          name: 'stopC',
          type: 'bar',
          label: {
            normal: {
              show: showLable,
              position: 'inside'
            }
          },
          data: this.stopCArray
        }],
        dataZoom: [{
          type: "slider",
          show: true,
          xAxisIndex: [0],
          start: 0,
          end: 100,
          textStyle: {
            color: "#ccd7d7"
          }
        }]
      }; // 4.传入数据

      myChart.setOption(option);
    }
  },
  mounted: function mounted() {
    this.myEchartsInit();
  }
};
