export default {
  name: 'Echarts',
  methods: {
    myEchartsInit: function myEchartsInit() {
      // 2.基于准备好的dom，初始化echarts实例
      var myChart = this.$echarts.init(document.getElementById('main')); //3.配置数据

      var showLable = true;
      var option = {
        title: {
          text: 'ECharts 入门示例'
        },
        tooltip: {},
        legend: {
          data: ['销量']
        },
        xAxis: {
          type: 'category',
          data: ["衬衫", "羊毛衫", "雪纺衫", "裤子", "高跟鞋", "袜子"]
        },
        yAxis: {
          type: 'value'
        },
        //Y轴
        series: [{
          name: '销量',
          type: 'bar',
          label: {
            normal: {
              show: showLable,
              position: 'inside'
            }
          },
          data: [0.1, 0.1, 0.1, 0.1, 10, 20]
        }]
      }; // 4.传入数据

      myChart.setOption(option);
    }
  },
  mounted: function mounted() {
    this.myEchartsInit();
  }
};
